    //----------------------- MyMethods-----------------------
    public boolean assignAllData(Record ref)
    {
        String errMsg="Please select an option!";                      
                
        try
        {
            if(p3_r2==0)                     
                { throw new Exception(errMsg); }
            else
                {  ref.phase3[1]=p3_r2;        }                                      
        }catch(Exception e)
        {
            //System.out.println(e.getMessage());
            SPA.panelController(e.getMessage(), new int[]{2,0,1,3,1,0});
            return(false);
        }
        
        SPA.panelController(null, new int[]{0,0,0,3,1,0});
        return(true);
    }
    
    void resetPanel()
    {
        p3_r2=0;
        btnGp_p3_q2.clearSelection();      
    }
    
    void loadPanel(Record r)
    {           
        p3_r2=r.phase3[1];         
        
        switch(r.phase3[1]) 
        {
            case 1 : radio_p3_q2_1.setSelected(true); break;
            case 2 : radio_p3_q2_2.setSelected(true); break;
            case 3 : radio_p3_q2_3.setSelected(true); break;            
        }       
    }
    
