    //----------------------- MyMethods-----------------------
    public boolean assignAllData(Record ref)
    {
        String errMsg="Please select an option!";                      
                
        try
        {
            if(p3_r3==0)                     
                { throw new Exception(errMsg); }
            else
                { 
                    ref.phase3[2]=p3_r3;                    
                }                                      
        }catch(Exception e)
        {
            //System.out.println(e.getMessage());
            SPA.panelController(e.getMessage(), new int[]{2,0,1,3,1,0});
            return(false);
        }
        
        SPA.panelController(null, new int[]{0,0,0,3,1,0});
        return(true);
    }
    
    void resetPanel()
    {
        p3_r3=0;
        btnGp_p3_q3.clearSelection();      
    }
    
    void loadPanel(Record r)
    {           
        p3_r3=r.phase3[2];        
        
        switch(r.phase3[2]) 
        {
            case 1 : radio_p3_q3_1.setSelected(true); break;
            case 2 : radio_p3_q3_2.setSelected(true); break;
            case 3 : radio_p3_q3_3.setSelected(true); break;
            case 4 : radio_p3_q3_4.setSelected(true); break;            
        }       
    }
    
